    }

    createGroups(groups){
        return axios.post(EMPLOYEE_API_BASE_URL, groups);
    }

    getGroupsById(GroupsId){
        return axios.get(EMPLOYEE_API_BASE_URL + '/' + GroupsId);
    }

    updateGroups(groupe, GroupsId){
        return axios.put(EMPLOYEE_API_BASE_URL + '/' + GroupsId, groupe);
