import React, { Component } from 'react'
import ServiceGroup from '../Service/ServiceGroup'

class CreateGroup extends Component {


    constructor(props) {
        super(props)
    
        this.state = {
             name:'',
             address:''
             
        }
    }
    

    UpdateName = (e)=>{

        this.setState({
            name : e.target.value

        })
    }

    UpdateAddress = (e)=>{

        this.setState({
            address : e.target.value

        })
    }

    SaveGroup = (e)=>{

        e.preventDefault();
        let group={name:this.state.name,address:this.state.address}
        ServiceGroup.createGroups(group).then(res=>{
            this.props.history.push('/group')
        })
    }

    render() {
        return (
            <div className='container'>
                <form>
                    <div className='form-group'>
                        <input type="text" value={this.state.name} onChange={this.UpdateName}></input>
                        <input type="text" value={this.state.address} onChange={this.UpdateAddress}></input>
                        <button type="submit" onClick={this.SaveGroup}></button>
                    </div>
                </form>
            </div>
        )
    }
}

export default CreateGroup
