import React, { Component } from 'react'
import Groups from './Group'

class ListGroup extends Component {

    state = {
        isLoading: true,
        groups: []
      };
    
 /*     async componentDidMount() {
        Groups.getGroups().then(resp=>{
          this.setState({
            groups:resp.data,
           isLoading:false
          })
        })
    }
 */
    render() {

        const {groups, isLoading} = this.state;

        if(isLoading){
            return <h1>Loading</h1>
        }
        return (
            <div className="container mt-3">
                <h1 className="text-danger">Les Groups</h1>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Adresse</th>
                            <th>City</th>
                            <th>stateOrProvince</th>
                            <th>country</th>
                            <th>postalCode</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                                groups.map(group=>{
                                    return(
                                        <tr>
                                            <td>{group.id}</td>
                                            <td>{group.name}</td>
                                            <td>{group.address}</td>
                                            <td>{group.city}</td>
                                            <td>{group.stateOrProvince}</td>
                                            <td>{group.country}</td>
                                            <td>{group.postalCode}</td>
                                        </tr>
                                    )
                                })
                        }
                        
                    </tbody>

                </table>
                
            </div>
        )
    }
}

export default ListGroup
